import React from 'react'
import "./style.css"
import Heromain from './Heromain';
import About from './About';
import Service from './Service';
import Team from './Team';
import Contacts from './Contacts';
import Footer from './Footer';
import Axios from 'axios';
//import Authorization from './Authorization';

//import {Routes, Route, Link} from "react-router-dom";


//import { useState } from "react";
//import $ from "jquery";



//import {FaSun} from "react-icons/fa";
//import {FaMoon} from "react-icons/fa";
import {FiMenu} from "react-icons/fi";
import {HiOutlineMenuAlt1} from "react-icons/hi";

const Homapage = () => {
       //alert(isLoggedIn);
        /*const [name, setName] = useState("");
        const [result, setResult] = useState("");*/    
        /*const handleChange = (e) => {
            setName(e.target.value);
        };
  
        const handleSumbit = (e) => {
            e.preventDefault();
            const form = $(e.target);
            $.ajax({
                type: "POST",
                url: form.attr("action"),
                data: form.serialize(),
                success(data) {
                    setResult(data);
                },
            });
        };*/
        //
        
        let isLoggedIn=0;
        const getlog = () => {
          //alert("hi");
          Axios.post("http://localhost:3001/logged",{
            lgname:"flag",
          }).then((response) =>{
            //alert(response.data.message);
            isLoggedIn=response.data.message;
            //alert("is " + isLoggedIn);
            if(response.data.message!==0){
              //alert("yes");
              document.querySelector(".rega").innerHTML=response.data.message;
              
             }
           });
        };

        const renderAuthButton = () => {
          //alert("isss " + isLoggedIn);
          {getlog()};
          if (!isLoggedIn) {
            return <a href='/authorization' className="rega">Log in</a>;
          } 
          else {
            return <a href='/authorization' className="rega">Log out</a>;
          }
        }
        
        
        const stickyhead = () =>{
            var header=document.querySelector(".headbar");
            header.classList.toggle("sticky",window.scrollY > 0);
        };
        const menuscrolfunc = () =>{
            document.querySelector(".headmenu").classList.add("headmenu_toggle");
            document.querySelector("#menuicon").style.visibility = "hidden";
            document.querySelector("#menualticon").style.visibility="visible";
        };
        const menuclosfunc = () =>{
            document.querySelector(".headmenu").classList.remove("headmenu_toggle");
            document.querySelector("#menuicon").style.visibility = "visible";
            document.querySelector("#menualticon").style.visibility="hidden";
        };
        
  return (
    <div onWheel={() => stickyhead()} onScroll={() => stickyhead()} className="homapage">
      <div className="canvas">
        <div className="headbar">
          <div className="headlogo">
            <a href="https://ibb.co/4Mrp97L"><img src="https://i.ibb.co/Sv0xbpg/logo5.png" alt="pp" border="0" height="50rem" width="200rem"/></a>
          </div>
          <div class="headmenu">
            <a href="#" className="headmenuopt">Home</a>
            <a href="#About" className="headmenuopt">About</a>
            <a href="#Service" className="headmenuopt">Services</a>
            <a href="#Contacts" className="headmenuopt">Contacts</a>
          </div>
          <div className="menubut">
            <FiMenu class="fimenu" onClick={getlog}/*onClick={() => menuscrolfunc()}*/ id="menuicon"></FiMenu>
            <HiOutlineMenuAlt1 class="fimenu" onClick={() => menuclosfunc()} id="menualticon"></HiOutlineMenuAlt1>
          </div>
          <div className="reg">
              {/*<a href='/authorization' className="rega">Sign in</a>*/}
              {renderAuthButton()};
          </div>

          
        </div>
        <Heromain></Heromain>
        <About></About>
        <Service></Service>
        <Team></Team>
        <Contacts></Contacts>
        <Footer></Footer>
        
      </div>
    </div>
  )
}

export default Homapage
