import React, {useState} from 'react';

import Homapage from './Homapage'
import Forgotpass from './Forgotpass';
import {Routes, Route, Link} from "react-router-dom";
import Axios from 'axios';


import $, { event } from "jquery";
const Authorization = () => {

    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [result, setResult] = useState("");
  
    /*const handleChange = (e) => {
        setName(e.target.value);
    };
    const handleChangep = (e) => {
        setPass(e.target.value);
    };
  
    const handleSumbit = (e) => {
        e.preventDefault();
        const form = $(e.target);
        $.ajax({
            type: "POST",
            url: form.attr("action"),
            data: form.serialize(),
            success(data) {
                if(data==0){
                    setResult("entered");
                    window.open("/","_self");
                }
                else if(data==1){
                    setResult("empty name");
                }
                else if(data==2){
                    setResult("empty pass");
                }
                else if(data==3){
                    setResult("username or pass is incorrect");
                }
                document.querySelector(".logfrm").reset();
                
            },
        });
    };
    const register = () => {
        Axios.post("http://localhost3001/register",{
            username: usernameReg,
            password: passwordReg,
        }).then((response)=>{
            console.log(response);
        });
    };*/

  return (
    <div className="authorization">
        <form action="http://localhost/login.php" method="post" className="logfrm" onSubmit={(event) => handleSumbit(event)}>
            <h2>Log In</h2>
            <label htmlFor="name">UserID: </label><br/>
            <input type="email" id="name" name="name" placeholder="Insert Your User ID" onChange={(event) => handleChange(event)}/><br/>
            <label htmlFor="pass">Pass: </label><br/>
            <input type="password" id="pass" name="pass" placeholder="Insert Your Password" onChange={(event) => handleChangep(event)}/><br/>
            <p id="frmchk">{result}</p> 
            <button id="log2reg"><a href="/signup">SignUp</a></button>
            <button type="submit">LogIn</button><br/>
            <a href="/forgotpass">Forgot password?</a>
        </form>   
        <br/><br/>
    </div>
  )
}

export default Authorization
