import React, { useState } from 'react';
import "./style.css"
import Calendar from 'react-calendar';
//import 'react-calendar/dist/Calendar.css';
import Axios from 'axios';

const calender = () => {
    const [value, onChange] = useState(new Date());
    // State for date selected by user
    const [selectedDate, setSelectedDate] = useState();

    // State for text above calander
    const [calendarText, setCalendarText] = useState(`No Date is selected`);
    const handleDateChange = (value) => {
        setSelectedDate(value);
        setCalendarText(`The selected Date is ${value.toDateString()}`);
        let valDate=value.toDateString();
        let valDay=valDate.substring(0, 3);
        //alert(valDay);
        bookSlot(valDay);
    };

    const bookSlotday = (valDay) => {
        alert(valDay);
        Axios.post('http://localhost:3001/slotday',{
            bookday:valDay,
        }).then((response) =>{
            alert(response.data.message);
        });
        window.open("/bookslot", "_top");
    };
  return (
    <div className="calender">
        <h2>Pick Your Date</h2>
        <Calendar 
            onChange={onChange} value={value}  
            onChange={handleDateChange}
        />
        {/*<Calendar
            onChange={handleDateChange}
        />*/}
    </div>
  )
}

export default calender
