import React, {useState} from 'react';

import Homapage from './Homapage'
import {Routes, Route, Link} from "react-router-dom";
import Axios from 'axios';
//import $, { event } from "jquery";

const Authorization = () => {

    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [result, setResult] = useState("");
  
    const displayInfo = () => {
        console.log(username + password);
        alert(username + password);
    }
    const addEmployee = () => {
        alert("yes");
        Axios.post('http://localhost:3001/create',{
            username:username,
            password:password
        }).then(() =>{
            console.log("success");
            alert("succes");
        });
        document.querySelector(".logfrm").reset();
    };
    const getEmployee = () => {
        alert("yess");
        Axios.post('http://localhost:3001/log',{
            username:username,
            password:password
        }).then(() =>{
            console.log("success");
            alert("succes");
        });
        document.querySelector(".logfrm").reset();
    };
  return (
    <div className="authorization">
        <form className="logfrm">
            <h2>Log In</h2>
            <label htmlFor="name">UserID: </label><br/>
            <input type="email" id="name" name="name" placeholder="Insert Your User ID" onChange={(event) => {setUsername(event.target.value);}}/><br/>
            <label htmlFor="pass">Pass: </label><br/>
            <input type="password" id="pass" name="pass" placeholder="Insert Your Password" onChange={(event) => {setPassword(event.target.value);}}/><br/>
            <p id="frmchk">{result}</p> 
            <button id="log2reg" onClick={getEmployee}>SignUp</button>
            <button type="submit" onClick={addEmployee}>LogIn</button><br/>
            <a href="/">Forgot password?</a>
        </form>   
        <form className="frgtfrm">
            <h2>Password Recover</h2>
            <label htmlFor="name">Email ID: </label><br/>
            <input type="email" id="name" name="name" placeholder="Enter Your Email id" onChange={(event) => {setUsername(event.target.value);}} required /><br/>
            <button type="submit">Submit</button><br/>
            <a href="#">go to login</a>
            
        </form>  
    </div>
  )
}

export default Authorization
