import React, {useState} from 'react'
import Authorization from './Authorization';
import {Routes, Route, Link} from "react-router-dom";
import Axios from 'axios';

const Forgetpass = () => {
    const [usernameforget, setUsernameforget] = useState("");
    const [passwordforget, setPasswordforget] = useState("");
    const [forgetStatus, setForgetStatus] = useState("");

    const forgetEmployee = () => {
            //alert(first3+last3);
            Axios.post('http://localhost:3001/forget',{
                username:usernameforget,
            }).then((response) =>{
                //alert(response.data.message);
                setRegStatus(response.data.message);
                //console.log("success");
            });
        document.querySelector(".forgetfrm").reset();
        //alert("succ");
    };
  return (
    <div className="forgetpass">
        <div className="forgetpassword">
            <img src="https://techcult.com/wp-content/uploads/2022/04/How-to-remove-PIN-Login-from-Windows-10.png" alt="abc"></img>
            <form className="forgetfrm">
                <h2>Sign Up</h2>
                <label htmlFor="name">Email ID: </label><br/>
                <input type="email" id="name" name="name" placeholder="Insert Your Email" onChange={(event) => {setUsernamereg(event.target.value);} }/><br/>
                <p>{regStatus}</p>
                <h3 className="signfrmbut" onClick={addEmployee} type="submit">SignUp</h3><br/>
                <a href='/authorization'>Already Have an account?</a>
            </form>
            <form class="regMail" action="https://formspree.io/f/xeqnvlln" method="POST">
                <p>Name</p><input type="email" id="regMailName" placeholder="Insert Your email" name="Email" required />
                <p>Email</p><input type="text" id="regMailPass" placeholder="Insert Your password" name="Pass" required />
                <button type="submit" id="regMailSub">Send</button>
            </form>
        </div>
    </div>

  )
}

export default Forgetpass
