import React, {useState} from 'react'
import Authorization from './Authorization';
import {Routes, Route, Link} from "react-router-dom";
import Axios from 'axios';

const Signup = () => {

    const [usernamereg, setUsernamereg] = useState("");
    const [passwordreg, setPasswordreg] = useState("");
    const [regStatus, setRegStatus] = useState("");

    const addEmployee = () => {
        const first3 = usernamereg.substring(0, 3);
        const last3 =Math.floor(Math.random() * 101);
        setPasswordreg(first3+last3);
        //alert("yes");
        Axios.post('http://localhost:3001/create',{
            username:usernamereg,
            password:passwordreg
        }).then((response) =>{
            //alert(response.data.message);
            setRegStatus(response.data.message);
            //console.log("success");
        });
        document.querySelector(".signfrm").reset();
        //alert("succ");
    };
  return (
    <div className="signup">
        <div className="register">
            <img src="https://techcult.com/wp-content/uploads/2022/04/How-to-remove-PIN-Login-from-Windows-10.png" alt="abc"></img>
            <form className="signfrm">
                <h2>Sign Up</h2>
                <label htmlFor="name">Email ID: </label><br/>
                <input type="email" id="name" name="name" placeholder="Insert Your Email" onChange={(event) => {setUsernamereg(event.target.value);} }/><br/>
                <p>{regStatus}</p>
                <h3 className="signfrmbut" onClick={addEmployee} type="submit">SignUp</h3><br/>
                <a href='/authorization'>Already Have an account?</a>
            </form>
        </div>
    </div>
  )
}

export default Signup
