import React, { useState } from 'react';
import "./style.css"
import Calendar from 'react-calendar';
//import 'react-calendar/dist/Calendar.css';
import Axios from 'axios';

const calender = () => {
    const [value, onChange] = useState(new Date());
    // State for date selected by user
    const [selectedDate, setSelectedDate] = useState();
    const [employeeList,setEmployeeList] = useState([]);

    // State for text above calander
    const [calendarText, setCalendarText] = useState(`No Date is selected`);
    const handleDateChange = (value) => {
        setSelectedDate(value);
        setCalendarText(`The selected Date is ${value.toDateString()}`);
        let valDate=value.toDateString();
        let valDay=valDate.substring(0, 3);
        //alert(valDay);
        bookSlotday(valDay);
    };

    const bookSlotday = (valDay) => {
        alert(valDay);
        Axios.post('http://localhost:3001/bookday',{
            bookday:valDay,
        }).then((response) =>{
            setEmployeeList(response.data);
            alert(JSON.stringify(response.data));
        });
    };
  return (
    <div className="calender">
        <h2>Pick Your Date</h2>
        <Calendar 
            //onChange={onChange} value={value}  
            onChange={handleDateChange}
        />
        {/*<Calendar
            onChange={handleDateChange}
        />*/}
        
        <div className="emptyslots">
            <div className="emptyslot">
            {employeeList.map((item, index) => {
                     return <div id="employeelist" key={index}>
                        {item.roomno} <br/> 
                        {item.slot1} <br/> 
                        {item.slot2} <br/> 
                        {item.slot3} <br/> 
                        {item.slot4} <br/> 
                        {item.slot5}</div>;
            })}
            </div>
        </div>

    </div>
  )
}

export default calender
